package com.advent.code.days.sixth;

import org.junit.jupiter.api.Test;

import java.io.IOException;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class AdventOfCode6Part2Test {

    AdventOfCode6 adventOfCode6 = new AdventOfCode6();

    @Test
    public void shouldBeOkWithSample() throws IOException {
        String input = "bvwbjplbgvbhsrlpgdmjqwftvncz";
        int indexMarker = adventOfCode6.returnMarkerIndex(input, 14);
        assertEquals(23, indexMarker);

        assertEquals(19, adventOfCode6.returnMarkerIndex("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 14));
        assertEquals(23, adventOfCode6.returnMarkerIndex("nppdvjthqldpwncqszvftbrmjlhg", 14));
        assertEquals(29, adventOfCode6.returnMarkerIndex("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 14));
        assertEquals(26, adventOfCode6.returnMarkerIndex("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 14));
    }

    @Test
    public void shouldBeOkWithRightInput() throws IOException {
        String input = "";
        int indexMarker = adventOfCode6.returnMarkerIndex(input, 14);
        assertEquals(5, indexMarker);
    }
}