package com.advent.code.days.sixth;

import org.junit.jupiter.api.Test;

import java.io.IOException;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class AdventOfCode6Test {

    AdventOfCode6 adventOfCode6 = new AdventOfCode6();

    @Test
    public void shouldBeOkWithSample() throws IOException {
        String input = "mjqjpqmgbljsphdztnvjfqwrcgsmlb";
        int indexMarker = adventOfCode6.returnMarkerIndex(input, 4);
        assertEquals(7, indexMarker);

        assertEquals(5, adventOfCode6.returnMarkerIndex("bvwbjplbgvbhsrlpgdmjqwftvncz", 4));
        assertEquals(6, adventOfCode6.returnMarkerIndex("nppdvjthqldpwncqszvftbrmjlhg", 4));
        assertEquals(10, adventOfCode6.returnMarkerIndex("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 4));
        assertEquals(11, adventOfCode6.returnMarkerIndex("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 4));
    }

    @Test
    public void shouldBeOkWithRightInput() throws IOException {
        String input = "";
        int indexMarker = adventOfCode6.returnMarkerIndex(input, 14);
        assertEquals(5, indexMarker);
    }
}